mapper.select:
    select 
    t1.id, t1.name, t1.loginname, t1.pwd, t1.img, t1.email, t1.birthday, t1.sex, t1.mobile, t1.qianming, t1.createtime, 
    t1.lastlogintime, t1.zhuceip, t1.loginip, t1.flag, t1.jifen,t3.id groupid ,t3.groupname
    from jbbs_user t1 left join jbbs_group_user t2 on t1.id=t2.userid left join jbbs_group t3 on t2.groupid=t3.id
    where 1=1

		and  t1.id = #{id,jdbcType=INTEGER}


		and  t1.flag = #{flag,jdbcType=VARCHAR}


		and  t1.loginname = #{loginname,jdbcType=VARCHAR}

SELECT t1.id, t1.name, t1.loginname, t1.pwd, t1.img, t1.email, t1.birthday, t1.sex, t1.mobile, t1.qianming, t1.createtime, t1.lastlogintime, t1.zhuceip, t1.loginip, t1.flag, t1.jifen, t3.groupname, t3.id groupid FROM jbbs_user t1 LEFT JOIN jbbs_group_user t2 ON t1.id = t2.userid LEFT JOIN jbbs_group t3 ON t2.groupid = t3.id WHERE 1 = 1
and t1.id = #{bean.id,jdbcType=INTEGER}
and t1.name = #{bean.name,jdbcType=VARCHAR}
and t1.loginname = #{bean.loginname,jdbcType=VARCHAR}
and t1.flag = #{bean.flag,jdbcType=VARCHAR}
ORDER BY t1.id LIMIT ${pageNo}, ${pageSize}

    SELECT 
	  COUNT(*) 
	FROM
	  jbbs_user t1 
	  LEFT JOIN jbbs_group_user t2 
	    ON t1.id = t2.userid 
	  LEFT JOIN jbbs_group t3 
	    ON t2.groupid = t3.id 
	WHERE 1 = 1 

        and t1.id = #{bean.id,jdbcType=INTEGER}


        and t1.name = #{bean.name,jdbcType=VARCHAR}


        and t1.loginname = #{bean.loginname,jdbcType=VARCHAR}


        and t1.flag = #{bean.flag,jdbcType=VARCHAR}
